@keyframes animate {
  0% {
    opacity: 0;
    transform: rotateY(90deg);
    filter: blur(10px);
  }
  100% {
    opacity: 1;
    transform: rotateY(0deg);
    filter: blur(0);
  }
}

@keyframes translateDown {
  from {
    opacity: 0;
    transform: translate(-10px);
  }

  to {
    opacity: 1;
    transform: translate(0);
  }
}

*,
::before,
::after {
  box-sizing: border-box;
  font-family: "Bangers", cursive;
}

body {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: url(/7d11113407263d50449183f743bfd8b0.jpg);
  background-size: cover;
  animation: animate 0.3s;
}

.game-title {
  padding-bottom: 60px;
  font-family: "Bangers", cursive;
  letter-spacing: 0.5em;
  font-size: 40px;
  color: #eee;
  animation: translateDown 1s;
}

.btns {
  font-family: "Bangers", cursive;
  margin: 25px 25px;
  cursor: pointer;
  padding: 15px 20px;
  font-size: 25px;
  letter-spacing: 0.2em;
}

.new-game {
  border-color: #ff9800;
  color: orange;
  transition: all 0.2s;
}

.new-game:hover {
  background: #ff9800;
  color: white;
}

.rules {
  border-color: #2196f3;
  color: dodgerblue;
  transition: all 0.2s;
}

.rules:hover {
  background: #2196f3;
  color: white;
}

.game {
  display: flex;
  justify-content: space-between;
  width: 1000px;
  margin: auto;
}

aside {
  width: 150px;
  background: lightblue;
  letter-spacing: 2px;
  border-radius: 10px;
}

.avatar {
  width: 100%;
  max-height: 160px;
  display: block;
  margin: auto;
  padding: 5px;
}

.grid-container {
  display: grid;
  grid-template-rows: repeat(9, 75px);
  grid-template-columns: repeat(9, 75px);
  margin: 15px;
  border: 2px solid black;
  width: 700px;
}
.grid-container > div {
  outline: 1px solid rgba(0, 0, 0, 0.2);
  display: flex;
  align-items: center;
  justify-content: center;
  align-content: center;
  background-color: lightblue;
}
.grid-container > div.highlight {
  box-shadow: inset 0 0 5px red;
}
.grid-container > div img {
  width: 90%;
  height: 90%;
  object-fit: contain;
}

.ironman {
  background-color: white;
}

.captain-america {
  background-color: black;
}

#rulesModal {
  position: fixed;
  width: 100vw;
  height: 100vh;
  backdrop-filter: blur(10px);
  align-items: center;
  display: none;
}

#rulesModal.open {
  display: flex;
  animation: animate 0.3s;
}

#rulesModal button {
  border-color: #ff0000;
  color: red;
  font-size: 22px;
  position: absolute;
  top: 5px;
  right: 6px;
  width: 25px;
  height: 25px;
  cursor: pointer;
  font-family: "Bangers", cursive;
  transition: all 0.2s;
}

#closeRules:hover {
  background: #ff0000;
  color: white;
}

#rulesModal .content {
  width: 700px;
  margin: auto;
  background-color: white;
  text-align: left;
  padding: 30px;
  position: relative;
  background: rgb(6, 82, 150);
  background: linear-gradient(
    17deg,
    rgba(6, 82, 150, 1) 0%,
    rgba(5, 216, 250, 1) 61%,
    rgba(15, 241, 255, 1) 100%
  );
  color: #ff9800;
  text-shadow: 1px 1px black;
}

.content h1 {
  text-align: center;
  padding-bottom: 25px;
  font-family: "Bangers", cursive;
  letter-spacing: 0.2em;
  word-spacing: 5px;
}

ul {
  font-family: "Bangers", cursive;
  letter-spacing: 0.1em;
  word-spacing: 3px;
  font-size: 18px;
  line-height: 110%;
  animation: animate 0.5s;
}

h3 {
  animation: animate 0.5s;
}

.shield {
  width: 100%;
  height: auto;
  display: block;
  margin: auto;
  background: white;
}

.box {
  text-align: center;
}

.box img {
  display: block;
  margin: 10px auto;
  width: 120px;
  height: 120px;
  object-fit: contain;
}

.box .value {
  display: inline-block;
  color: white;
  padding: 4px 8px;
  width: 90%;
  background: rgb(6, 82, 150);
  background: linear-gradient(
    17deg,
    rgba(6, 82, 150, 1) 0%,
    rgba(5, 216, 250, 1) 61%,
    rgba(15, 241, 255, 1) 100%
  );
  border-radius: 10px;
}

.current {
  box-shadow: 0 0 20px red;
}

#gameOverModal,
#retaliationModal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: none;
  /* backdrop-filter: blur(10px); */
}

#gameOverModal.open,
#retaliationModal.open {
  display: block;
}

#gameOverModal > div,
#retaliationModal > div {
  position: absolute;
  background: rgb(6, 82, 150);
  background: linear-gradient(
    17deg,
    rgba(6, 82, 150, 1) 0%,
    rgba(5, 216, 250, 1) 61%,
    rgba(15, 241, 255, 1) 100%
  );
  padding: 40px;
  width: 300px;
  margin-left: -150px;
  margin-top: -154px;
  top: 50%;
  left: 50%;
  text-align: center;
  letter-spacing: 2px;
}

#retaliationModal button {
  cursor: pointer;
  border: 0;
  color: black;
  padding: 10px 20px;
  letter-spacing: 2px;
}

#gameOverModal button {
  margin: 25px 0;
}

#attack {
  background: #ff0000;
  margin-left: 3px;
}

#defend {
  background-color: lightgreen;
  margin-right: 3px;
}

.protected {
  background-color: green !important;
}

